/* Timeline Section */
.timeline-container {
    position: relative;
    padding: 2rem 0;
}

.timeline {
    position: relative;
    max-width: 1200px;
    margin: 0 auto;
}

.timeline::after {
    content: '';
    position: absolute;
    width: 4px;
    background: var(--gradient-1);
    top: 0;
    bottom: 0;
    left: 50%;
    margin-left: -2px;
    border-radius: 2px;
}

.timeline-item {
    padding: 10px 40px;
    position: relative;
    background-color: inherit;
    width: 50%;
    opacity: 0;
    transform: translateY(30px);
    transition: all 0.5s ease;
}

.timeline-item.show {
    opacity: 1;
    transform: translateY(0);
}

.timeline-item::after {
    content: '';
    position: absolute;
    width: 25px;
    height: 25px;
    right: -12.5px;
    background: var(--primary);
    border: 4px solid var(--highlight);
    top: 25px;
    border-radius: 50%;
    z-index: 1;
    transition: all 0.3s ease;
}

.timeline-item:hover::after {
    transform: scale(1.3);
    background: var(--highlight);
}

.left {
    left: 0;
}

.right {
    left: 50%;
}

.left::before {
    content: "";
    height: 0;
    position: absolute;
    top: 30px;
    width: 0;
    z-index: 1;
    right: 30px;
    border: medium solid rgba(255, 255, 255, 0.1);
    border-width: 10px 0 10px 10px;
    border-color: transparent transparent transparent rgba(255, 255, 255, 0.1);
}

.right::before {
    content: "";
    height: 0;
    position: absolute;
    top: 30px;
    width: 0;
    z-index: 1;
    left: 30px;
    border: medium solid rgba(255, 255, 255, 0.1);
    border-width: 10px 10px 10px 0;
    border-color: transparent rgba(255, 255, 255, 0.1) transparent transparent;
}

.right::after {
    left: -12.5px;
}

.timeline-content {
    padding: 2rem;
    background: rgba(255, 255, 255, 0.05);
    position: relative;
    border-radius: 15px;
    border: 1px solid rgba(255, 255, 255, 0.1);
    backdrop-filter: blur(10px);
    transition: all 0.3s ease;
}

.timeline-content:hover {
    background: rgba(255, 255, 255, 0.08);
    transform: scale(1.02);
    box-shadow: 0 20px 40px rgba(0, 0, 0, 0.3);
}

.timeline-year {
    position: absolute;
    top: -40px;
    left: 50%;
    transform: translateX(-50%);
    background: var(--gradient-2);
    padding: 0.5rem 1rem;
    border-radius: 20px;
    font-weight: 700;
    font-size: 0.9rem;
    white-space: nowrap;
}

.timeline-header {
    display: flex;
    justify-content: space-between;
    align-items: start;
    margin-bottom: 1rem;
    flex-wrap: wrap;
    gap: 1rem;
}

.timeline-title {
    font-size: 1.5rem;
    font-weight: 700;
    background: var(--gradient-3);
    -webkit-background-clip: text;
    -webkit-text-fill-color: transparent;
    background-clip: text;
}

.timeline-company {
    font-size: 1.2rem;
    color: var(--text);
    margin-bottom: 0.5rem;
}

.timeline-duration {
    font-size: 0.9rem;
    color: var(--text-secondary);
    display: flex;
    align-items: center;
    gap: 0.5rem;
}

.timeline-achievements {
    margin-top: 1rem;
}

.achievement-item {
    position: relative;
    padding-left: 1.5rem;
    margin-bottom: 0.8rem;
    line-height: 1.6;
    color: var(--text-secondary);
}

.achievement-item::before {
    content: '✨';
    position: absolute;
    left: 0;
    top: 0;
}

.timeline-tech-stack {
    display: flex;
    flex-wrap: wrap;
    gap: 0.5rem;
    margin-top: 1rem;
}

.timeline-tech {
    padding: 0.3rem 0.8rem;
    background: rgba(102, 126, 234, 0.2);
    border: 1px solid #667eea;
    border-radius: 15px;
    font-size: 0.85rem;
    color: #667eea;
}

/* Mobile Timeline */
@media screen and (max-width: 768px) {
    .timeline::after {
        left: 31px;
    }

    .timeline-item {
        width: 100%;
        padding-left: 70px;
        padding-right: 25px;
    }

    .timeline-item::before {
        left: 60px;
        border: medium solid rgba(255, 255, 255, 0.1);
        border-width: 10px 10px 10px 0;
        border-color: transparent rgba(255, 255, 255, 0.1) transparent transparent;
    }

    .left::after, .right::after {
        left: 18.5px;
    }

    .right {
        left: 0%;
    }

    .timeline-year {
        position: relative;
        top: 0;
        left: 0;
        transform: none;
        display: inline-block;
        margin-bottom: 1rem;
    }
}

/* Skills Section */
.skills-container {
    display: grid;
    grid-template-columns: repeat(auto-fit, minmax(250px, 1fr));
    gap: 2rem;
}

